<?phpfunction punkteFuerTipp($ergebnis, $tipp) {	if($ergebnis == $tipp) {		return 4; //genauer Tipp	}	$e = explode(":", $ergebnis);	$t = explode(":", $tipp);		$e1 = $e[0];	$e2 = $e[1];	$t1 = $t[0];	$t2 = $t[1];	$e_diff = $e1 - $e2;	$t_diff = $t1 - $t2;	if( ($e_diff == 0) && ($t_diff == 0) ) {		return 3; //3 Punkte unentschieden	}	if( ($e1 > $e2) && ($t1 > $t2) ) {		if( $e_diff == $t_diff ) {			return 3; //Heimsieg 3 Punkte		} else {			return 2; //Heimsieg 2 Punkte		}	}	if( ($e1 < $e2) && ($t1 < $t2) ) {		if( $e_diff == $t_diff ) {			return 3; //Auswaertssieg 3 Punkte		} else {			return 2; //Auswaertssieg 2 Punkte		}	}	return 0;}function bubblesort($a1,$a2) { 	for($i = sizeof($a1); $i >= 1; $i--) { 		for($j = 1; $j <= $i; $j++) { 			if($a1[$j-1] > $a1[$j]) { 				$t = $a1[$j-1]; 				$t2 = $a2[$j-1]; 				$a1[$j-1] = $a1[$j]; 				$a2[$j-1] = $a2[$j]; 				$a1[$j] = $t; 				$a2[$j] = $t2; 			} 		} 	} 	return $a2;} ?>